sources.styles.styl:
* {
  font-size: 15px;
  padding: 0;
  margin: 0;
}
.header {
  max-width: 1250px;
  min-height: 250px;
  margin: 0 auto;
  background: #fff;
  text-align: center;
}
.header .topline {
  margin: 0 auto;
  width: 99%;
  margin-top: 20px;
  background: #000;
  height: 3px;
}
.header .title {
  display: flex;
  align-items: center;
  padding: 2%;
}
.header .title .image1 {
  height: 130px;
  margin-right: 5%;
}
.header .title .image2 {
  height: 150px;
}
.header .line {
  margin: 0 auto;
  width: 100%;
  border: 1px solid #d3d3d3;
}
.header .metaData {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 2px auto;
}
.header .metaData p {
  font-size: 1rem;
  font-family: 'Oswald', sans-serif;
  letter-spacing: 0.1rem;
  font-weight: 300;
}
.header .metaData strong {
  font-size: 1.3rem;
  font-family: 'Roboto', sans-serif;
  font-weight: 900;
}
.header .metaData span {
  margin: auto 0.5%;
  color: #f00;
  text-shadow: 1px 0 0 #f00, -1px 0 0 #f00, 0 1px 0 #f00, 0 -1px 0 #f00, 0.5px 0.5px #f00, -0.5px -0.5px 0 #f00, 0.5px -0.5px 0 #f00, -0.5px 0.5px 0 #f00;
}
.header .lastText {
  display: grid;
  grid-template-columns: 96% 4%;
  margin: 2px 0;
  position: relative;
}
.header .lastText p {
  font-size: 0.85rem;
  font-weight: 300;
  font-family: 'Oswald', sans-serif;
  letter-spacing: 0.1rem;
}
.header .lastText .t1 {
  grid-column: 1/3;
}
.header .lastText .t2 {
  grid-column: 2/3;
  position: absolute;
  z-index: 3;
  font-family: 'Roboto', sans-serif;
  font-weight: 900;
}
.header .bottomLine {
  margin: 0 auto;
  width: 100%;
  background: #000;
  height: 1.3rem;
}
.main {
  max-width: 1250px;
  height: 100%;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 0.95fr 0.8fr 1fr 1.3fr;
  grid-template-rows: 230px 1px 450px 50px 110px 345px 75px;
  grid-gap: 1%;
  background: #fff;
}
.main span {
  font-family: 'Roboto', sans-serif;
  font-weight: 900;
  color: #f00;
}
.article1 {
  grid-column: 1/4;
  grid-row: 1/2;
  display: grid;
  grid-template-columns: 1fr 30%;
  position: relative;
  background-color: #efe9e3;
}
.article1 .text {
  margin-left: 3%;
}
.article1 .text h2 {
  font-size: 4.5rem;
}
.article1 .text h2 span {
  font-size: 4.5rem;
  font-family: none;
}
.article1 .text p {
  width: 100%;
  font-size: 1.6rem;
  color: rgba(0,0,0,0.7);
}
.article1 .text p span {
  font-size: 1.4rem;
  font-family: none;
}
.article1 img {
  position: relative;
  bottom: 0;
  margin-top: 2%;
  width: 100%;
  height: 95%;
  object-fit: contain;
  overflow: hidden;
}
.article2 {
  grid-column: 4/5;
  grid-row: 1/2;
  margin-top: 4%;
  padding-left: 3%;
  padding-bottom: 3%;
  border-left: 3px solid #d3d3d3;
  border-bottom: 3px solid #d3d3d3;
}
.article2 img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.article3 {
  grid-column: 1/2;
  grid-row: 3/4;
}
.article3__container {
  height: 100%;
}
.article3 h3 {
  font-size: 3.5vh;
  font-weight: 900;
  line-height: 90%;
  margin-bottom: 4%;
}
.article3 p {
  font-size: 1.4vh;
}
.article3 p span {
  font-weight: bold;
  font-size: 1.4vh;
}
.article3 p strong {
  font-size: 1.4vh;
}
.article3 img {
  margin-top: 10px;
  width: 100%;
  height: 60%;
  object-fit: cover;
}
.article4 {
  border-top: 8px solid #000;
  border-bottom: 8px solid #000;
  grid-column: 1/2;
  grid-row: 4/6;
}
.article4__text {
  display: flex;
  flex-direction: column;
}
.article4__text h3 {
  margin-top: 2%;
  color: #f00;
  font-weight: 900;
  font-size: 1.7vh;
  font-family: 'Roboto', sans-serif;
}
.article4__text h2 {
  font-size: 2.5vh;
  font-weight: 900;
  font-family: 'Roboto', sans-serif;
  line-height: 90%;
}
.article4__text p {
  margin-top: 2%;
  font-size: 1.5vh;
  font-weight: 600;
  font-family: 'Roboto', sans-serif;
}
.article4__text span {
  margin-top: 2%;
  align-self: flex-end;
  font-weight: bold;
  font-size: 1.8vh;
}
.article5 {
  grid-column: 1/2;
  grid-row: 6/8;
  padding-top: 10px;
  border-top: 1px solid #d3d3d3;
}
.article5 img {
  width: 100%;
  height: 100%;
}
.article6 {
  grid-column: 2/5;
  grid-row: 3/5;
  display: inline-block;
  overflow: hidden;
}
.article6__container {
  width: 100%;
  height: 100%;
  position: relative;
  z-index: 2;
}
.article6__container--image {
  object-fit: cover;
  width: 100%;
  height: 100%;
}
.article6__container--text {
  z-index: 3;
  position: absolute;
  bottom: 5%;
  left: 1%;
}
.article6__container--text h1 {
  font-size: 8vh;
  width: 70%;
  color: #006698;
  text-shadow: 1px 0 0 #fff, -1px 0 0 #fff, 0 1px 0 #fff, 0 -1px 0 #fff, 0.5px 0.5px #fff, -0.5px -0.5px 0 #fff, 0.5px -0.5px 0 #fff, -0.5px 0.5px 0 #fff;
}
.article6__container--text h3 {
  font-family: 'Roboto', sans-serif;
  font-size: 2.5vh;
  width: 30%;
  text-shadow: 1px 0 0 #fff, -1px 0 0 #fff, 0 1px 0 #fff, 0 -1px 0 #fff, 0.5px 0.5px #fff, -0.5px -0.5px 0 #fff, 0.5px -0.5px 0 #fff, -0.5px 0.5px 0 #fff;
}
.article6__container--text h3 span {
  font-size: 2.5vh;
  text-shadow: 1px 0 0 #fff, -1px 0 0 #fff, 0 1px 0 #fff, 0 -1px 0 #fff, 0.5px 0.5px #fff, -0.5px -0.5px 0 #fff, 0.5px -0.5px 0 #fff, -0.5px 0.5px 0 #fff;
}
.article7 {
  grid-column: 2/3;
  grid-row: 5/8;
  text-align: center;
  border-left: 1px solid #d3d3d3;
  padding-top: 8%;
}
.article7 span {
  color: #000;
  font-weight: 900;
}
.article7 img {
  width: 90%;
  height: 35%;
}
.article7 .text h1 {
  font-family: 'Roboto', sans-serif;
  font-weight: 900;
  padding-top: 7%;
  font-size: 6vh;
  line-height: 80%;
}
.article7 .text h2 {
  font-family: 'Roboto', sans-serif;
  font-weight: 900;
  font-size: 2.8vh;
}
.article7 .text p {
  font-family: 'Roboto', sans-serif;
  font-weight: 200;
  margin-top: 5%;
  font-size: 1.95vh;
}
.article7 .text p strong {
  font-size: inherit;
}
.article7 .text p span {
  font-size: inherit;
}
.article8 {
  grid-column: 3/5;
  grid-row: 5/7;
  display: grid;
  grid-template-columns: 3.1fr 0.9fr;
  grid-template-rows: 1.6fr 2.4fr;
  grid-gap: 1%;
  padding-left: 2%;
  border-bottom: 1px solid #d3d3d3;
  border-left: 1px solid #d3d3d3;
}
.article8 h1 {
  grid-column: 1/3;
  grid-row: 1/2;
  font-size: 6.5vh;
}
.article8 .text {
  grid-column: 1/2;
  columns: 2;
  text-align: justify;
  font-size: 1.8vh;
  padding-right: 1%;
}
.article8 .text strong {
  font-family: 'Oswald', sans-serif;
  letter-spacing: 1px;
}
.article8 .text p {
  font-size: 1.2rem;
}
.article8 .text .second {
  text-indent: 1.8rem;
}
.article8 .text .author {
  padding-bottom: 4px;
  border-bottom: 1px solid #808080;
}
.article8 .imgSection {
  grid-column: 2/3;
  display: inline;
}
.article8 .imgSection img {
  width: 100%;
}
.article8 .imgSection strong {
  font-family: 'Oswald', sans-serif;
}
.article8 .imgSection p {
  font-family: 'Roboto', sans-serif;
  font-weight: 400;
  font-size: 1rem;
}
.article8 .imgSection span {
  font-weight: 200;
  color: rgba(0,0,0,0.8);
}
.article9 {
  grid-column: 3/5;
  grid-row: 7/8;
  display: grid;
  grid-template-columns: 1.2fr 0.8fr;
  border-left: 1px solid #d3d3d3;
  padding: auto;
  background: #f8f2ef;
}
.article9 .clm1 {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  width: 100%;
}
.article9 .clm1 .writers {
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  height: 75px;
  width: 30%;
  background: #f00;
}
.article9 .clm1 .writers p {
  font-size: 1.2rem;
  text-align: center;
  border-bottom: none;
}
.article9 .clm1 p {
  width: 110%;
  padding-bottom: 2%;
  padding-top: 1%;
  border-bottom: 1px solid #d3d3d3;
}
.article9 .clm2 {
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-left: 2%;
  border-left: 1px solid #d3d3d3;
}
.article9 .clm2 p {
  width: 80%;
  padding-bottom: 2%;
  padding-top: 1%;
  border-bottom: 1px solid #d3d3d3;
}
